/**
 * Application
 * -----------------------------------------------------
 * http://www.akshitsethi.com
 */

// Toastr : Default Options
toastr.options = {
	"closeButton": false,
	"debug": false,
	"newestOnTop": true,
	"progressBar": false,
	"positionClass": "toast-bottom-right",
	"preventDuplicates": false,
	"onclick": null,
	"showDuration": "1000",
	"hideDuration": "1000",
	"timeOut": "5000",
	"extendedTimeOut": "1000",
	"showEasing": "swing",
	"hideEasing": "linear",
};

(function( $ ) {

	'use strict';

	// DOM Ready
	// ------------------------------------------------------------

	$( document ).ready( function() {

		// IOS Switches
		// ------------------------------------------------------------

		var elements = Array.prototype.slice.call(document.querySelectorAll('.as-form-ios'));
	    elements.forEach(function(html) {
    		var switchery = new Switchery(html);
	    });


		// Header : Sticky
		// ------------------------------------------------------------

		if( $( window ).width() <= 600 ) {
			$( '.as-header' ).stick_in_parent( { bottoming: false, offset_top: 0 } );
		} else if ( $( window ).width() > 600 && $( window ).width() < 783 ) {
			$( '.as-header' ).stick_in_parent( { bottoming: false, offset_top: 46 } );
		} else {
			$( '.as-header' ).stick_in_parent( { bottoming: false, offset_top: 32 } );
		}

		// Submission
		// ------------------------------------------------------------

		$( document ).on( 'click', '#as_wb_submit', function() {

			// ID
			var as_ID 		= $( this ).attr( 'data-tab' ).replace( '#', '' );

			// Form : Data
			var as_data 	= new FormData( $( '#' + as_ID + ' form' )[0] );

			// Append : Action
			as_data.append( 'action', 'as_wb_' + as_ID );

			console.log( as_data );

			// AJAX
			$.ajax( {
				type: 'POST',
				url: ajaxurl,
				data: as_data,
				processData: false,
				contentType: false,
				beforeSend: function() {
					$( '#' + as_ID ).block( {
						message: '<div class="as-strong" style="background: #ecf0f1; padding: 6px; color: #000;">Processing..</div>',
						css: {
							border: 'none',
							backgroundColor: 'none'
						},
						overlayCSS: {
							backgroundColor: '#eeeeee',
							opacity: '0.5',
							cursor: 'wait'
						}
					} );
				}
			} ).done( function( data ) {
				// Unblock
				$( '#' + as_ID ).unblock();

				// Success
				if( data.code == 'success' ) {
					toastr.success( '<strong>Hey!</strong> ' + data.response );

					// Remove : Input Class
					$( 'input, textarea, select' ).removeClass( 'changed-input' );
				} else {
					// Error
					toastr.error( '<strong>Oops!</strong> ' + data.response );
				}
			} );
		} );


		// Hack : Support
		// ------------------------------------------------------------

		$( document ).on( 'submit', '.as-support-form', function( e ) {
			e.preventDefault();
		} );


		// Change : Form
		// ------------------------------------------------------------

		$( 'form' ).on( 'change keyup keydown', 'input, textarea, select', function( e ) {
			// Cookie :State
			var $state = Cookies.get( 'as_wb_menu' );

			if ( $state ) {
				if ( $state != '#support' ) {
					$( this ).addClass( 'changed-input' );
				}
			} else {
				$( this ).addClass( 'changed-input' );
			}
		} );

		// Cookie : State
		var $state = Cookies.get( 'as_wb_menu' );

		// Check : Menu Position
		if( $state ) {
			// Button
			var $as_button = $( '#as_wb_submit' );

			$( '.as-main-menu li a' ).removeClass( 'active' );
			$( 'a[href="' + $state + '"]' ).addClass( 'active' );

			// Add : Button (data-tab)
			$( '#as_wb_submit' ).attr( 'data-tab', $state );

			// Modifications
			// Hack : About
			if ( $state == '#about' ) {
				$as_button.hide();
			} else {
				$as_button.show();
			}

			// Hack : Support
			if ( $state == '#support' ) {
				$as_button.val( 'Ask for Support' );
			} else {
				$as_button.val( 'Save Changes' );
			}

			// Load
			$( $state ).fadeIn();
		} else {
			$( '.as-main-menu li:first a' ).addClass( 'active' );
			$( '.as-tile:first' ).fadeIn();

			// Add : Options (data-tab)
			$( '#as_wb_submit' ).attr( 'data-tab', '#options' );
		}

		// Menu
		$( '.as-main-menu li a' ).click( function(e) {
			e.preventDefault();

			// Remove : Cookie
			Cookies.remove( 'as_wb_menu', { path: '/' } );

			var $selector 		= $( this );
			var $tab      		= $selector.attr( 'href' );
			var $as_button = $( '#as_wb_submit' );

			if ( $( '.changed-input' ).length ) {
				toastr.error( '<strong>Hey!</strong> You haven\'t saved your changes.' );
			} else {
				// Change : Menu Selection
				$( '.as-main-menu li a' ).removeClass( 'active' );
				$selector.addClass( 'active' );

				// Hide Tabs
				$( '.as-tile' ).hide();

				// Load : Selected Tab
				$( $tab ).fadeIn();

				// Set Cookie
				Cookies.set( 'as_wb_menu', $tab, { path: '/' } );

				// State : Button (add)
				$( '#as_wb_submit' ).attr( 'data-tab', $tab );

				// Modifications
				// Hack : About
				if ( $tab == '#about' ) {
					$as_button.hide();
				} else {
					$as_button.show();
				}

				// Hack : Support
				if ( $tab == '#support' ) {
					$as_button.val( 'Ask for Support' );
				} else {
					$as_button.val( 'Save Changes' );
				}
			}
		} );

		// Mobile Navigation
		$( '.as-mobile-menu a' ).click( function() {
			$( '.as-main-menu' ).slideToggle();
		} );


		// Registration
		// -------------------------------------------------

		$( document ).on( 'submit', '.as-register-form', function( e ) {
			e.preventDefault();

			// Form : Data
			var as_data = new FormData( $( this )[0] );

			// Append : Action
			as_data.append( 'action', 'as_wb_register' );

			// AJAX
			$.ajax( {
				type: 'POST',
				url: ajaxurl,
				data: as_data,
				processData: false,
				contentType: false,
				beforeSend: function() {
					$( '.as-product-register' ).block( {
						message: '<div class="as-strong" style="background: #ecf0f1; padding: 6px; color: #000;">Processing..</div>',
						css: {
							border: 'none',
							backgroundColor: 'none'
						},
						overlayCSS: {
							backgroundColor: '#eeeeee',
							opacity: '0.5',
							cursor: 'wait'
						}
					} );
				}
			} ).done( function( data ) {
				// Unblock
				$( '.as-product-register' ).unblock();

				if( data.type == 'success' ) {					
					// Notification
					toastr.success( '<strong>Hey!</strong> ' + data.message );

					// HTML
					$( '#as-ajax-response' ).html( '<div class="as-notice as-success"><strong>Hey!</strong> ' + data.message + '</div>' );

					// Reload
					setTimeout( function() {
						window.location.reload(false);
					}, 5000 );
				} else {
					// Notification
					toastr.error( '<strong>Oops!</strong> ' + data.message );

					// HTML
					$( '#as-ajax-response' ).html( '<div class="as-notice as-error"><strong>Oops!</strong> ' + data.message + '</div>' );
				}
			} );
		} );

	} );

} )( jQuery );